mapper.select:
/* commonDao.selectLeftMenuList: 좌측 메뉴조회 */ SELECT COMP_ID , MENU_SEQ , UPPR_MENU_ID , MENU_ID , MENU_NM , CASE WHEN USE_YN != 'Y' THEN 'N' ELSE USE_YN END AS USE_YN , CASE WHEN MENU_Lvl1 != 'Y' THEN 'N' ELSE MENU_Lvl1 END AS MENU_LVL1 , CASE WHEN MENU_Lvl2 != 'Y' THEN 'N' ELSE MENU_Lvl2 END AS MENU_LVL2 , CASE WHEN MENU_Lvl3 != 'Y' THEN 'N' ELSE MENU_Lvl3 END AS MENU_LVL3 , CONVERT(VARCHAR, REG_DTM, 120) AS REG_DTM , UPD_ID , CONVERT(VARCHAR, UPD_DTM, 120) AS UPD_DTM FROM MENU_MANAGE
AND USE_YN = 'Y'
ORDER BY COMP_ID, MENU_SEQ
