mapper.select:
/* fam001Dao.selectVegetFarmList: 순번 조회 */ SELECT A.TOT_CNT , A.RNUM , A.COMP_ID , A.SVC_SEQ , A.SVC_ID , A.SVC_NM , A.PAK_PATH , A.USE_YN , A.SVC_DAO , A.SVC_XML , A.REG_ID , A.REG_DTM , A.UPD_ID , A.UPD_DTM , A.ROW_STATUS FROM ( SELECT COUNT(*) OVER() AS TOT_CNT , ROW_NUMBER() OVER(ORDER BY SVC_SEQ ASC) AS RNUM , COMP_ID , SVC_SEQ , SVC_ID , SVC_NM , PAK_PATH , CASE WHEN USE_YN != 'Y' THEN 'N' ELSE USE_YN END AS USE_YN , SVC_DAO , SVC_XML , REG_ID , DATE_FORMAT(REG_DTM, '%Y-%m-%d %T.%s') AS REG_DTM , UPD_ID , DATE_FORMAT(UPD_DTM, '%Y-%m-%d %T.%s') AS UPD_DTM , 'R' AS ROW_STATUS FROM SVC_MANAGE
AND (SVC_ID = #{searchSvcId} OR SVC_NM LIKE CONCAT('%',#{searchSvcId}, '%'))
AND USE_YN = #{useYn})
) A ORDER BY A.SVC_SEQ ASC limit #{curPage}, #{pageSize}

    	/* fam001Dao.selectSvcSeq: 순번 조회 */

    	SELECT ISNULL(MAX(SVC_SEQ), 0) + 1 
	      FROM SVC_MANAGE

mapper.insert:
    	/* fam001Dao.saveVegetFarm: 채소팜 등록 */
	     INSERT 
	       INTO SVC_MANAGE
    	 (     COMP_ID			/* 소속회사 */
    	     , SVC_SEQ
    	   	 , SVC_ID
    	 	 , SVC_NM
    	 	 , PAK_PATH
    	 	 , USE_YN
    	 	 , SVC_DAO
    	 	 , SVC_XML

    	 ) 
    	 VALUES 

    	 (    	 
    	 	   ${@com.jjplatform.admin.util.CommonUtils@getCommVal('COMP')}
    	 	 , CASE WHEN IFNULL(#{item.svcSeq},'') != '' THEN #{item.svcSeq}
    	 	  		ELSE (
			    	 		SELECT IFNULL(MAX(SVC_SEQ), 0) + (#{idx} + 1) 
						      FROM SVC_MANAGE  ALIAS_FOR_SUBQUERY
						     WHERE COMP_ID = ${@com.jjplatform.admin.util.CommonUtils@getCommVal('COMP')}
			    	 	 )
				END
    	 	 , #{item.svcId}
    	 	 , #{item.svcNm}
    	 	 , #{item.pakPath}
    	 	 , #{item.useYn}
    	 	 , #{item.svcDao}
    	 	 , #{item.svcXml}

    	 )

    	 ON DUPLICATE KEY UPDATE  
    	         SVC_ID = VALUES(SVC_ID)
    	       , SVC_NM = VALUES(SVC_NM)
    	       , PAK_PATH = VALUES(PAK_PATH)
    	       , USE_YN = VALUES(USE_YN)
    	       , SVC_DAO = VALUES(SVC_DAO)
    	       , SVC_XML = VALUES(SVC_XML)
