mapper.select:
/* menu001Dao.selectMenuList: 메뉴 조회 */ WITH RECURSIVE CTE AS ( SELECT COMP_ID , UPPR_MENU_ID , MENU_SEQ , MENU_ID , MENU_NM , CASE WHEN USE_YN != 'Y' THEN 'N' ELSE USE_YN END AS USE_YN , CASE WHEN MENU_LVL1 IS NULL OR MENU_LVL1 != 'Y' THEN 'N' ELSE MENU_LVL1 END AS MENU_LVL1 , CASE WHEN MENU_LVL2 IS NULL OR MENU_LVL2 != 'Y' THEN 'N' ELSE MENU_LVL2 END AS MENU_LVL2 , CASE WHEN MENU_LVL3 IS NULL OR MENU_LVL3 != 'Y' THEN 'N' ELSE MENU_LVL3 END AS MENU_LVL3 , MENU_AUTH , REG_ID , DATE_FORMAT(REG_DTM, '%Y-%m-%d %T') AS REG_DTM , UPD_ID , DATE_FORMAT(UPD_DTM, '%Y-%m-%d %T') AS UPD_DTM , 'R' AS ROW_STATUS , CAST(MENU_SEQ AS CHAR) AS LVL_SEQ FROM MENU_MANAGE WHERE IFNULL(UPPR_MENU_ID, '') = '' AND COMP_ID = ${@com.jjplatform.admin.util.CommonUtils@getCommVal('COMP')} UNION ALL SELECT A.COMP_ID , A.UPPR_MENU_ID , A.MENU_SEQ , A.MENU_ID , A.MENU_NM , CASE WHEN A.USE_YN != 'Y' THEN 'N' ELSE A.USE_YN END AS USE_YN , CASE WHEN A.MENU_LVL1 IS NULL OR A.MENU_LVL1 != 'Y' THEN 'N' ELSE A.MENU_LVL1 END AS MENU_LVL1 , CASE WHEN A.MENU_LVL2 IS NULL OR A.MENU_LVL2 != 'Y' THEN 'N' ELSE A.MENU_LVL2 END AS MENU_LVL2 , CASE WHEN A.MENU_LVL3 IS NULL OR A.MENU_LVL3 != 'Y' THEN 'N' ELSE A.MENU_LVL3 END AS MENU_LVL3 , A.MENU_AUTH , A.REG_ID , DATE_FORMAT(A.REG_DTM, '%Y-%m-%d %T') AS REG_DTM , A.UPD_ID , DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d %T') AS UPD_DTM , 'R' AS ROW_STATUS , CONCAT(CAST(B.MENU_SEQ AS CHAR),'_',CAST(A.MENU_SEQ AS CHAR)) AS LVL_SEQ FROM MENU_MANAGE A INNER JOIN CTE B ON A.COMP_ID = B.COMP_ID AND A.UPPR_MENU_ID = B.MENU_ID WHERE A.COMP_ID = ${@com.jjplatform.admin.util.CommonUtils@getCommVal('COMP')} AND IFNULL(A.UPPR_MENU_ID, '') != '' ) SELECT * , COUNT(*) OVER() AS TOT_CNT
FROM CTE
AND COMP_ID = ${@com.jjplatform.admin.util.CommonUtils@getCommVal('COMP')}
AND (MENU_ID = #{searchMenuId} OR MENU_NM LIKE CONCAT('%',#{searchMenuId}, '%'))
AND USE_YN = #{searchUseYn}
ORDER BY COMP_ID, LVL_SEQ LIMIT #{curPage}, #{pageSize}

    	/* menu001Dao.selectMenuSeq: 순번 조회 */
    	SELECT IFNULL(MAX(MENU_SEQ), 0) + 1 
	      FROM MENU_MANAGE  ALIAS_FOR_SUBQUERY
	     WHERE COMP_ID = #{compId}
	       AND UPPR_MENU_ID = #{upprMenuId}

mapper.insert:
    	/* menu001Dao.saveMenu: 메뉴등록*/

		     INSERT 
		       INTO MENU_MANAGE
	    	 (      COMP_ID
	    	      , UPPR_MENU_ID
	    	      , MENU_SEQ
	    	   	  , MENU_ID
	    	 	  , MENU_NM
	    	 	  , PAK_PATH
	    	 	  , USE_YN
	    	 	  , MENU_Lvl1
		       	  , MENU_Lvl2
		       	  , MENU_Lvl3

	    	 ) 
	    	 VALUES 

	    	 (    	#{compId}
	    		  , #{upprMenuId} 
	    	 	  , #{menuSeq}
	    	 	  , #{menuId}
	    	 	  , #{menuNm}
	    	 	  , #{pakPath}
	    	 	  , #{useYn}
	    	 	  , #{menuLvl1}
	    	 	  , #{menuLvl2}
	    	 	  , #{menuLvl3}

	    	 )

	    	 ON DUPLICATE KEY UPDATE  
	    	         COMP_ID = VALUES(COMP_ID)
	    	       , MENU_ID = VALUES(MENU_ID)
	    	       , MENU_NM = VALUES(MENU_NM)
	    	       , PAK_PATH = VALUES(PAK_PATH)
	    	       , USE_YN = VALUES(USE_YN)
	    	       , MENU_Lvl1 = VALUES(MENU_Lvl1)
		       	   , MENU_Lvl2 = VALUES(MENU_Lvl2)
		       	   , MENU_Lvl3 = VALUES(MENU_Lvl3)
