mapper.select:
    	/* menu001Dao.selectMenuSeq: 순번 조회 */

    	SELECT ISNULL(MAX(MENU_SEQ), 0) + 1 
	      FROM MENU_MANAGE

mapper.insert:
    	/* menu001Dao.menuSave: 메뉴등록*/

    	MERGE 
    	 INTO MENU_MANAGE AS A1
	    USING (
	    		 SELECT #{compId} AS COMP_ID
	    		      , #{upprMenuId} AS UPPR_MENU_ID
	    		      , #{menuSeq} AS MENU_SEQ
	    	  ) AS A
	       ON A1.MENU_SEQ = A.MENU_SEQ
	     WHEN MATCHED THEN
	    	  UPDATE 
	    	     SET MENU_ID = #{menuId}
	    	       , UPPR_MENU_ID = A.UPPR_MENU_ID
	    	       , MENU_NM = #{menuNm}
	    	       , USE_YN = #{useYn}
	    	       , MENU_LVL1 = #{menuLvl1}
	    	       , MENU_LVL2 = #{menuLvl2} 
	    	       , MENU_LVL3 = #{menuLvl3} 

	    WHEN NOT MATCHED THEN
	    	 INSERT 
	    	 (     COMP_ID
			 	 , UPPR_MENU_ID
	    	 	 , MENU_SEQ
	    	   	 , MENU_ID
	    	 	 , MENU_NM
	    	 	 , USE_YN
	    	 	 , MENU_LVL1
	       	  	 , MENU_LVL2
	       	  	 , MENU_LVL3

	    	 ) 
	    	 VALUES 
	    	 (	   A.COMP_ID
	    	     , A.UPPR_MENU_ID
	    	     , A.MENU_SEQ
	    	 	 , #{menuId}
	    	 	 , #{menuNm}
	    	 	 , #{useYn}
	    	 	 , #{menuLvl1}
	    	 	 , #{menuLvl2}
	    	 	 , #{menuLvl3}

	    	 )
	    	 ;
