mapper.select:
SELECT A.TOT_CNT , A.RNUM , A.SVC_SEQ , A.SVC_ID , A.SVC_NM , A.PAK_PATH , A.USE_YN , A.SVC_DAO , A.SVC_XML , A.REG_ID , A.REG_DTM , A.UPD_ID , A.UPD_DTM , A.ROW_STATUS FROM ( SELECT COUNT(*) OVER() AS TOT_CNT , ROW_NUMBER() OVER(ORDER BY SVC_SEQ ASC) AS RNUM , SVC_SEQ , SVC_ID , SVC_NM , PAK_PATH , USE_YN , SVC_DAO , SVC_XML , REG_ID
, DATE_FORMAT(REG_DTM, '%Y-%m-%d %T.%s') AS REG_DTM , UPD_ID
, DATE_FORMAT(UPD_DTM, '%Y-%m-%d %T.%s') AS UPD_DTM , 'R' AS ROW_STATUS FROM SVC_MANAGE
AND (SVC_ID = #{svcId} OR SVC_NM LIKE CONCAT('%',#{svcId}, '%'))
) A ORDER BY A.SVC_SEQ ASC limit #{curPage}, #{pageSize}

    	/* svc001Dao.selectSvcSeq: 순번 조회 */
    	SELECT IFNULL(MAX(SVC_SEQ), 0) + 1 
	      FROM SVC_MANAGE  ALIAS_FOR_SUBQUERY

mapper.insert:
    	/* svc001Dao.svcSave: 서비스등록*/


	     INSERT 
	       INTO SVC_MANAGE
    	 (     SVC_SEQ
    	   	 , SVC_ID
    	 	 , SVC_NM
    	 	 , PAK_PATH
    	 	 , USE_YN
    	 	 , SVC_DAO
    	 	 , SVC_XML

    	 ) 
    	 VALUES 

    	 (    	 
    	 	   CASE WHEN IFNULL(#{svcSeq}, '') = '' THEN #{svcSeq} ELSE #{svcSeq} END
    	 	 , #{svcId}
    	 	 , #{svcNm}
    	 	 , #{pakPath}
    	 	 , #{useYn}
    	 	 , #{svcDao}
    	 	 , #{svcXml}

    	 )

    	 ON DUPLICATE KEY UPDATE  
    	         SVC_ID = VALUES(SVC_ID)
    	       , SVC_NM = VALUES(SVC_NM)
    	       , PAK_PATH = VALUES(PAK_PATH)
    	       , USE_YN = VALUES(USE_YN)
    	       , SVC_DAO = VALUES(SVC_DAO)
    	       , SVC_XML = VALUES(SVC_XML)
